import React,{useState} from 'react';
import './App.css';
import Header from './components/Header';
import DiscoverCard from './components/DiscoverCard';
import Filter from './components/Filter';
import RetreatList from './components/RetreatList';
import Footer from './components/Footer';

function App() {
  const retreatsData = [
    {
      title: 'Forest Yoga Retreat',
      date: 'June 10-15, 2024',
      location: 'Redwood Forest, CA',
      price: '$1200',
    },
    {
      title: 'Lake Meditation Retreat',
      date: 'July 21-26, 2024',
      location: 'Lake Tahoe, NV',
      price: '$1400',
    },
    {
      title: 'Spa Detox Retreat',
      date: 'August 5-10, 2024',
      location: 'Aspen, CO',
      price: '$1800',
    },
  ];

  const [search, setSearch] = useState('');
  const [dateFilter, setDateFilter] = useState(false);
  const [typeFilter, setTypeFilter] = useState('');
  const handleSearchChange = (searchTerm) => {
    setSearch(searchTerm);
  };

  const handleDateFilterChange = () => {
    setDateFilter(!dateFilter);
  };

  const handleTypeFilterChange = () => {
    // For simplicity, toggle between 'Yoga' and '' (no filter)
    setTypeFilter((prev) => (prev === 'Yoga' ? '' : 'Yoga'));
  };

  const filteredRetreats = retreatsData
  .filter((retreat) => {
    return (
      retreat.title.toLowerCase().includes(search.toLowerCase()) &&
      (!dateFilter || new Date(retreat.date) >= new Date()) &&
      (!typeFilter || retreat.type === typeFilter)
    );
  })

  return (
    <div className="container1">
      <Header />
      <div className='main-content'>
      <DiscoverCard />
      <Filter onDateFilterChange={handleDateFilterChange}
          onTypeFilterChange={handleTypeFilterChange}
          onSearchChange={handleSearchChange}/>
      <RetreatList retreats={filteredRetreats} />
      </div>
      <Footer />
    </div>
  );
}

export default App;
