import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RetreatsFilter = ({setSelectedRange, selectedRange}) => {

  // Handle changes in the date range selection
  const handleRangeChange = (event) => {
    const range = event.target.value;
    setSelectedRange(range);
    fetchDataAndFilter(range);
  };

  // // Effect to fetch data initially (if needed)
  // useEffect(() => {
  //   fetchDataAndFilter(selectedRange); // Fetch initially if there's a default range
  // }, [selectedRange]);

  return (
      <select className="dropbtn" onChange={handleRangeChange} value={selectedRange}>
        <option value="">Filter by date</option>
        <option value="2023-2024">2023-2024</option>
        <option value="2024-2025">2024-2025</option>
      </select>


  );
};

export default RetreatsFilter;
